import React, { useEffect, useState } from "react";
import { Carousel } from "react-bootstrap";
import { useParams } from "react-router";

const ProjectDetails = () => {
  const { id } = useParams();
  // console.log(aboutId);
  const [projectDetails, setProjectDetails] = useState([]);

  const [project, setProject] = useState({});

  useEffect(() => {
    fetch("/data.json")
      .then((res) => res.json())
      .then((data) => setProjectDetails(data));
  }, []);

  useEffect(() => {
    const foundProject = projectDetails.find((project) => project.id === id);
    setProject(foundProject);
  }, [projectDetails]);
  return (
    <div className="container">
      <div className="heading">
        <h2 className="text-center">Project Details</h2>
      </div>

      <div className="row justify-content-center mt-3">
        <div className="col-md-7">
          <div class="card border-success mb-3">
            <div class="card-header bg-transparent border-success">
              <h5 class="card-title">{project?.title}</h5>
            </div>
            <div class="card-body text-success"></div>
            <div class="card-footer bg-transparent border-success">
              <div className="col-md-7 col-12 text-center">
                <div className="d-flex justify-content-between">
                  <a
                    className="link2 btn border"
                    target="_blank"
                    rel="noreferrer"
                    href={project?.live}
                  >
                    Live
                  </a>
                  <a
                    className="link2"
                    target="_blank"
                    rel="noreferrer"
                    href={project?.frontend}
                  >
                    Frontend Code
                  </a>
                  {project?.backend ? (
                    <a
                      className="link2"
                      target="_blank"
                      rel="noreferrer"
                      href={project?.backend}
                    >
                      Backend Code
                    </a>
                  ) : (
                    ""
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* <Carousel variant="dark">
        {project?.imgs &&
          project?.imgs.map((img) => (
            <Carousel.Item variant="dark">
              <div>
                <img
                  className="d-block w-100"
                  height="500px"
                  src={img}
                  alt=""
                />
              </div>
            </Carousel.Item>
          ))}
      </Carousel> */}
      <h2></h2>
    </div>
  );
};

export default ProjectDetails;
